mapper.select:
SELECT COUNT(*)
FROM SPACE
WHERE HOST_NO = #{memNo}

mapper.insert:
INSERT INTO SPACE (SPACE_NO
				 , SPACE_TITLE
				 , SPACE_SUBTITLE
				 , SPACE_INFO
				 , HOUR_PRICE
				 , MAX_PEOPLE
				 , ADDRESS_DEFAULT
				 , ADDRESS_DETAIL
				 , TEL
				 , HASHTAG
				 , LATITUDE
				 , LONGITUDE
				 , OPEN_TIME
				 , CLOSE_TIME
				 , HOST_NO
				 , STYPE_NO)
		   VALUES (SEQ_SNO.NEXTVAL
		   		 , #{spaceTitle}
		   		 , #{spaceSubTitle}
		   		 , #{spaceInfo}
		   		 , #{hourPrice}
		   		 , #{maxPeople}
		   		 , #{addressDefault}
		   		 , #{addressDetail}
		   		 , #{tel}
		   		 , #{hashtag}
		   		 , #{latitude}
		   		 , #{longitude}
		   		 , #{openTime}
		   		 , #{closeTime}
		   		 , #{hostNo}
		   		 , #{stypeNo})

mapper.update:
	UPDATE SPACE
    SET 
	  SPACE_TITLE = #{spaceTitle}
	, SPACE_SUBTITLE = #{spaceSubTitle}
	, SPACE_INFO = #{spaceInfo}
	, HOUR_PRICE = #{hourPrice}
	, MAX_PEOPLE = #{maxPeople}
	, ADDRESS_DEFAULT = #{addressDefault}
	, ADDRESS_DETAIL = #{addressDetail}
	, TEL = #{tel}
	, HASHTAG = #{hashtag}
	, SPACE_STATUS = 'W'
	, LATITUDE = #{latitude}
	, LONGITUDE = #{longitude}
	, OPEN_TIME = #{openTime}
	, CLOSE_TIME = #{closeTime}
	, DENY_MESSAGE = NULL
	, STYPE_NO = #{stypeNo}
	 WHERE SPACE_NO = #{spaceNo}

	UPDATE SPACE_ATTACHMENT
    SET ATTACHMENT_RENAME = #{attachmentReName}
  , ATTACHMENT_UPLOAD_DATE = SYSDATE
	WHERE SPACE_NO = #{spaceNo}
	AND ATTACHMENT_NO = #{attachmentNo}

mapper.select:
SELECT COUNT(*)
  FROM SPACE
 WHERE LATITUDE BETWEEN #{min_lat} AND #{max_lat}
   AND LONGITUDE BETWEEN #{min_lng} AND #{max_lng}
   AND SPACE_STATUS = 'Y'
